INSERT INTO `myblog_article` VALUES ('5', '1535440552.jpg', '【JQuery】播放器实现我的电台', '1535440552', '作品', '利用JQuery制作一个Web播放器,播放器可以禁止在微信页面中上拉回弹利用到了touchmove事件.....', '<p style=\"text-align: left;\">电台地址入口&nbsp;<span style=\"text-decoration: underline; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(255, 0, 0); border: none;\"><a href=\"http://www.xhyonline.com/radio\" target=\"_self\" style=\"text-decoration: underline; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(255, 0, 0); border: none;\">点击跳转</a></span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180828/1535440261930023.png\" title=\"1535440261930023.png\" alt=\"1535440261930023.png\" width=\"500\" height=\"865\"/></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180828/1535440453961471.png\" title=\"1535440453961471.png\" alt=\"1535440453961471.png\" width=\"500\" height=\"809\"/></p>');
INSERT INTO `myblog_article` VALUES ('6', '1535441180.png', '【JavaScript】禁止微信浏览器下拉回弹效果', '1535441180', '经验', '本文实例为大家分享了JavaScript禁止微信浏览器下拉回弹的效果.....', '<p><span style=\"color: rgb(79, 79, 79); text-align: justify; background-color: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">本文实例为大家分享了JavaScript禁止微信浏览器下拉回弹的效果</span></p><p><span style=\"color: rgb(79, 79, 79); text-align: justify; background-color: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">方法一:</span></p><pre class=\"brush:js;toolbar:false\">&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;overscroll&nbsp;=&nbsp;function(el){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;el.addEventListener(&#39;touchstart&#39;,&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;top&nbsp;=&nbsp;el.scrollTop;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;totalScroll&nbsp;=&nbsp;el.scrollHeight;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;currentScroll&nbsp;=&nbsp;top&nbsp;+&nbsp;el.offsetHeight;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(top&nbsp;===&nbsp;0)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;el.scrollTop&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;if(currentScroll&nbsp;===&nbsp;totalScroll){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;el.scrollTop&nbsp;=&nbsp;top&nbsp;-&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;el.addEventListener(&#39;touchmove&#39;,&nbsp;function(evt){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(el.offsetHeight&nbsp;&lt;&nbsp;el.scrollHeight){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;evt._isScroller&nbsp;=&nbsp;true;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;overscroll(document.querySelector(&#39;.scroll&#39;));//哪里需要可以局部滚动，添加一个“scroll”的class\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;document.body.addEventListener(&#39;touchmove&#39;,&nbsp;function(evt)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(!evt._isScroller){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;evt.preventDefault();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&lt;/script&gt;</pre><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 16px;\"><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">此方法的优缺点：</span><br/><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">优点：支持局部滚动；</span><br/><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">缺点：浏览器本身超出页面出现的滚动被禁用掉了，需要改为局部滚动，且局部滚动的地方需要加“scroll”的class。</span><br/><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">注：如果同一个页面多个局部滚动，需要将</span><br/><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; background-color: rgb(255, 255, 255); line-height: 21.6px; color: rgb(128, 0, 0);\">overscroll(document.querySelector(&#39;.scroll&#39;);<br/></span><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">改为</span><br/><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; background-color: rgb(255, 255, 255); line-height: 21.6px; color: rgb(128, 0, 0);\">for(var i=0;i&lt;document.querySelectorAll(&#39;.scroll&#39;).length;i++){<br/>overscroll(document.querySelectorAll(&#39;.scroll&#39;)[i]);<br/>}</span></span><span style=\"color: rgb(79, 79, 79); font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);\"></span><br/></span></p><p><span style=\"font-size: 16px; box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">方法二:</span></p><pre class=\"brush:js;toolbar:false\">&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;stopDrop(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;lastY;//最后一次y坐标点\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(document.body).on(&#39;touchstart&#39;,&nbsp;function(event)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;lastY&nbsp;=&nbsp;event.originalEvent.changedTouches[0].clientY;//点击屏幕时记录最后一次Y度坐标。\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(document.body).on(&#39;touchmove&#39;,&nbsp;function(event)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;y&nbsp;=&nbsp;event.originalEvent.changedTouches[0].clientY;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;st&nbsp;=&nbsp;$(this).scrollTop();&nbsp;//滚动条高度\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(y&nbsp;&gt;=&nbsp;lastY&nbsp;&amp;&amp;&nbsp;st&nbsp;&lt;=&nbsp;10)&nbsp;{//如果滚动条高度小于0，可以理解为到顶了，且是下拉情况下，阻止touchmove事件\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;lastY&nbsp;=&nbsp;y;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;event.preventDefault();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;lastY&nbsp;=&nbsp;y;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&lt;/script&gt;</pre><p><span style=\"font-size: 16px; box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"></span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">此方法的优缺点：</span><br/><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">优点：支持浏览器本身超出页面的滚动</span><br/><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; box-sizing: border-box; outline: 0px; word-break: break-all; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">缺点：不支持局部滚动</span></span></p><p><img src=\"/ueditor/php/upload/image/20180828/1535462871954107.png\" title=\"1535462871954107.png\" alt=\"QQ图片20180828204648.png\"/></p>');
INSERT INTO `myblog_article` VALUES ('2', '1535461965.jpg', 'Laravel开发框架中的数据库连贯操作', '1535108426', '笔记', 'Laravel数据库连贯操作,laravel中的数据库增删改查,Laravel5.3版本', '<pre class=\"brush:php;toolbar:false\">数据库连贯操作\r\n\r\n//insert()插入多条数据二维数组		插入一条数据$arr=array(&quot;name&quot;=&gt;&quot;玩具车&quot;,&quot;class&quot;=&gt;5);\r\n\r\n$arr=array(\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;array(\r\n\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&quot;name&quot;=&gt;&quot;玩具车模型&quot;,\r\n\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&quot;class&quot;=&gt;5\r\n\r\n	),\r\n\r\n	array(\r\n\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&quot;name&quot;=&gt;&quot;飞机模型&quot;,\r\n\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&quot;class&quot;=&gt;5\r\n\r\n	),\r\n\r\n\r\n\r\n);\r\n\r\n\r\n$data=DB::table(&quot;goods&quot;)-&gt;insert($arr);\r\n\r\n//修改update方法();	修改商品表,name为玩具02的修改name字段为玩具坦克\r\n\r\n\r\n$data=DB::table(&quot;goods&quot;)-&gt;where(&quot;name&quot;,&quot;=&quot;,&quot;玩具02&quot;)-&gt;update([&quot;name&quot;=&gt;&quot;玩具坦克&quot;]);	//多字段修改是一维数组\r\n\r\n//删除delete()	\r\n\r\n\r\n$data=DB::table(&quot;goods&quot;)-&gt;where(&quot;name&quot;,&quot;=&quot;,&quot;玩具坦克1&quot;)-&gt;delete();//删除name=玩具坦克1的这条字段\r\n\r\n$data=DB::table(&quot;goods&quot;)-&gt;delete();		//	清空数据表\r\n\r\n\r\n//&nbsp;求password这列字段的平均值\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;avg(&#39;password&#39;);\r\n\r\n\r\n//	求和\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;sum(&#39;id&#39;);\r\n\r\n\r\n//求最大值最小值	字段要求为int型\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;max(&#39;password&#39;);	\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;min(&#39;password&#39;);\r\n\r\n$data=DB::table(&#39;usertb&#39;)-&gt;find(&quot;3&quot;);	//find()查询的是id字段不是uid\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;where(&quot;uid&quot;,&quot;&gt;&quot;,&quot;4&quot;)-&gt;get();	//uid&gt;4的全部&nbsp;&nbsp;get()方法查询全部数据\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;where(&quot;uid&quot;,&quot;!=&quot;,&quot;4&quot;)-&gt;get();//uid不等于4的全部&nbsp;&nbsp;get()方法查询全部数据\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;where(&quot;uid&quot;,&quot;=&quot;,&quot;4&quot;)-&gt;first();//查询uid=4的第一条数据\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;where([[&quot;uid&quot;,&quot;&gt;&quot;,&quot;4&quot;],[&quot;uid&quot;,&quot;&lt;=&quot;,&quot;6&quot;]])-&gt;get();	//多条件查询[[]]条件A且条件B\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;where([[&quot;uid&quot;,&quot;&gt;&quot;,&quot;4&quot;],[&quot;name&quot;,&quot;like&quot;,&quot;%德%&quot;]])-&gt;get();	//多条件查询like条件A且条件B\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;where(&quot;uid&quot;,&quot;&gt;&quot;,&quot;5&quot;)-&gt;orwhere(&quot;uid&quot;,&quot;&gt;&quot;,&quot;6&quot;)-&gt;get();	//多条件查询&nbsp;&nbsp;&nbsp;条件A或B\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;whereNotBetween(&#39;uid&#39;,[5,7])-&gt;get();	//查询uid不在5-7之间的,成绩查询可用\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;whereBetween(&#39;uid&#39;,[5,7])-&gt;get();	//查询uid在5-7之间的\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;whereIn(&#39;uid&#39;,[5,7,6])-&gt;get();	//查询uid为5,7,6的数据&nbsp;,同理还有一个whereNotIn\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;pluck(&quot;name&quot;);	//只取name这一列字段的数据\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;count();	//计算表中数据总条数\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;orderBy(&quot;time&quot;,&quot;desc&quot;)-&gt;get();	//&nbsp;orderBy排序,不写desc默认是正序,从小到大\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;offset(3)-&gt;limit(3)-&gt;get();	//&nbsp;从offset()开始,limit截取几个	从3开始截取3个\r\n\r\n$data=DB::table(&quot;usertb&quot;)-&gt;limit(3)-&gt;get();	//&nbsp;默认从头开始截取3个\r\n\r\n//多表查询join()\r\n	\r\n//商品分类表	class		字段1(uid&nbsp;)&nbsp;字段2(分类名)\r\n\r\n//商品表	goods		字段1(id&nbsp;)&nbsp;字段2(商品名)&nbsp;	字段3(分类名class)\r\n\r\n$data=DB::table(&quot;goods&quot;)-&gt;join(&quot;class&quot;,&quot;class.uid&quot;,&quot;=&quot;,&quot;goods.class&quot;)-&gt;get();\r\n\r\n//查询商品表,结合分类表,分类表中的uid&nbsp;等于&nbsp;商品表中的class\r\n\r\n\r\n左连接leftjoin</pre>');
INSERT INTO `myblog_article` VALUES ('3', '1535113133.png', '【PHP】开源HTML5-PHP表白墙', '1535113133', '作品', '灵感来源于广西科技大学梁嘉平同学的留言墙,看过他写的邮箱表白墙后,自己也想动手写一个', '<p>一直没有时间把表白墙的链接扔上去,最近刚开学,终于抽了一点时间&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(255, 0, 0);\">&nbsp;</span><span style=\"color: rgb(255, 0, 0); text-decoration: underline;\"><a href=\"http://abc.xhyonline.com/mylove\" target=\"_self\" style=\"color: rgb(255, 0, 0); text-decoration: underline;\">表白墙地址入口</a></span></p><p><br/></p><p>留言中填写邮箱地址,PHP邮箱插件可以自动利用官方邮箱号发送邮件信息给对方</p><p><img src=\"/ueditor/php/upload/image/20180904/1536034124839732.png\" title=\"1536034124839732.png\" alt=\"1.png\"/></p><p><img src=\"/ueditor/php/upload/image/20180904/1536034160401358.png\" title=\"1536034160401358.png\" alt=\"2.png\"/></p>');
INSERT INTO `myblog_article` VALUES ('4', '1535113514.jpg', '【PHP】模拟POST请求', '1535113514', '经验', '在接口开发中,尤其是在调用第三方API中,我们经常能遇见http请求,文章罗列其中一种请求方式POST', '<pre class=\"brush:php;toolbar:false\">$data&nbsp;=&nbsp;array(\r\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;access_token&quot;=&gt;$access,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;to_yb_uid&quot;=&gt;8903609,\r\n	&quot;content&quot;=&gt;&quot;App唤起通知测试&quot;,\r\n	&quot;template&quot;=&gt;&quot;system&quot;\r\n);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n$data&nbsp;=&nbsp;http_build_query($data);&nbsp;//数组序列化\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n$options&nbsp;=&nbsp;array(\r\n&nbsp;&nbsp;&nbsp;&nbsp;&#39;http&#39;&nbsp;=&gt;&nbsp;array(\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;method&#39;&nbsp;=&gt;&nbsp;&#39;POST&#39;,&nbsp;&nbsp;&nbsp;&nbsp;//不能改成GET\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;header&#39;&nbsp;=&gt;&nbsp;&#39;Content-type:application/x-www-form-urlencoded&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;content&#39;&nbsp;=&gt;&nbsp;$data\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&#39;timeout&#39;&nbsp;=&gt;&nbsp;60&nbsp;*&nbsp;60&nbsp;//&nbsp;超时时间（单位:s）\r\n&nbsp;&nbsp;&nbsp;&nbsp;)\r\n);\r\n&nbsp;&nbsp;\r\n$url&nbsp;=&nbsp;&quot;https://openapi.yiban.cn/msg/letter&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n$context&nbsp;=&nbsp;stream_context_create($options);//创建流\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n$result&nbsp;=&nbsp;file_get_contents($url,&nbsp;false,&nbsp;$context);\r\nvar_dump($result);</pre><p><br/></p>');
INSERT INTO `myblog_article` VALUES ('7', '1535460191.png', '《Web全栈工程师的自我修养》', '1535459104', '经验', '《Web全栈工程师的自我修养》读书笔记转载', '<h2 style=\"white-space: normal; margin: 10px 0px; padding: 2px 5px; font-size: 18px; line-height: 1.5; background: none 0px 0px repeat scroll rgb(51, 153, 0); border-bottom: 0px none; color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif;\">什么是全栈工程师</h2><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><strong style=\"margin: 0px; padding: 0px;\">全栈工程师</strong>（Full-Stack Engineer）：一个能处理数据库、服务器、系统工程和客户端的所有工作的工程师。根据项目的不同，客户需要的可能是移动栈、Web栈，或者原生应用程序栈。<br/></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><strong style=\"margin: 0px; padding: 0px;\">全栈</strong>：表示为了完成一个项目，所需要的一系列技术的集合。应该从能力和思维方式两方面，来判定一个人是否是一个合格的全栈工程师。简单来说*全栈工程师就是可以独立完成一个产品的人。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/php/upload/image/20180828/1535459086640043.jpg\" title=\"1535459086640043.jpg\" alt=\"1535459086640043.jpg\" width=\"300\" height=\"217\"/></p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">1、Web开发流程</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"font-size: 14px;\">大中型互联网公司的产品研发流水线：产品设计--&gt;交互设计--&gt;视觉设计--&gt;前端开发、后台开发--&gt;测试--&gt;发布。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">产品经理</strong>：产品经理其实是对一个产品负根本责任的管理者。他通常的工作包括制订产品规划、协调多方资源、把控产品方向和质量细节，等等。有时候，他会从头策划一个新的产品，而更多的时候，他是在优化已有产品的一个部分。总之，在流水线中，产品经理需要从策划跟进到发布，是一个非常重要的角色。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">用户研究员</strong>：用户研究员的工作是研究用户行为，有时候他会从宏观的角度分析数据，有时候也从微观的角度分解用户场景，有时候会召集一些用户专门来访谈，或者观察用户对产品的使用情况。从输出品的角度来说，用户研究员一般输出用户研究报告来交付给产品经理和交互设计师，作为产品设计的目标参考。<br/>交互设计师：交互设计师常被简称为“交互”。他与视觉设计师最大的区别是，交互设计师更多着眼于如何优化用户界面的信息分布和操作流程。交互设计师的输出品一般是描述用户与网站“交互”过程的流程图，以及描述页面信息结构的线框图。输出的线框图会交付给视觉设计师。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">视觉设计师</strong>：在细分交互设计师和视觉设计师的大公司，视觉设计师根据交互设计师输出的线框图来做一些润色和设计，输出最终的产品视觉稿之后将视觉稿交付给前端工程师。在一些不细分交互设计师和视觉设计师的小公司，二者被统称为“设计师”，他们的职责就是负责整个用户界面的设计。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">前端工程师</strong>：产品视觉稿在得到产品经理和交互设计师等多方确认之后，会交给前端工程师，由前端工程师制作页面，实现视觉稿以及交互功能。从头衔上的变化就可以看出，这时候才真正开始编码。前端工程师需要非常熟悉HTML、CSS和JavaScript，以及性能、语义化、多浏览器兼容、SEO、自动化工具等广泛的知识。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">后台工程师</strong>：使用服务器编程语言，进行服务器功能的开发。在编程语言的选择上，很多公司都会出于团队已有成员的知识储备、程序员的供给量或者语言性能方面来进行选择。在这一方面，后台语言的选择是相对自由的一件事，不像前端工程师，为了页面兼容性，必须使用HTML和CSS。如果关注各大公司招聘信息的话，您就会了解，不同公司使用不同的后台语言，比如传统的C#和C++、Java、PHP，或者新潮的RoR和Python。小公司的后台工程师除了负责功能开发，可能还会负责服务器的配置和调试、数据库的配置和管理等工作。在大公司，这些工作会分别委派给后台工程师、运维工程师、数据库管理员（DBA）等岗位。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">运维工程师</strong>：运维工程师是跟服务器打交道的人，他会关注服务器的性能、压力、成本和安全等信息。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px;\">测试工程师</strong>：顾名思义，测试工程师保证产品的可用性，即使在小公司，这一职位也是不可或缺的。</span></p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">2、一专多长</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">我跟一位行业专家讨论过全栈工程师的话题，他不是很赞同全栈工程师这个方向。他认为，工程师应该有专精的技能和目标，如果初学者贪图大而全，反而样样不精。我理解他的担心，如果一个工程师没有坚实的基础（比如专业理论知识，对常用设计模式的理解，或者特定职业的基础知识），那么了解的非本专业技能越多，越容易迷失。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">所以我认为，<strong style=\"margin: 0px; padding: 0px;\">全栈工程师首先要“一专多长”</strong>。一专多长的意思是，工程师首先有一个专精的方向，在这个方向上足够精通之后（高级工程师级别），以此为突破点去学习更多的知识，增加自己的长处。如果还没有获得某个方向上足够深入的理解，就不要囫囵吞枣地去学习其他领域的知识。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">有些知识需要时间的积累，并不是快速阅读就可以掌握的。“全栈工程师”这个名词可能会引起读者的误解。勿在浮沙筑高台，“全栈”是一个长期积累的过程，是专精型工程师在不断解决问题的过程中积累知识和经验所形成的能力，而不是一蹴而就的过程。</span></p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">3、解决问题，而不是醉心技术</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">公司存在的意义就是解决问题，公司要解决用户的问题，而员工要解决公司的问题。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">公司的问题可能是降低成本、扩大用户群、增加成交量、优化性能，等等。不同的问题优先级不一样，投入同样的时间，有的项目能为公司增加上百万的收入，而有的项目却只能增加几万。</span></p><h2 style=\"white-space: normal; margin: 10px 0px; padding: 2px 5px; font-size: 18px; background: none 0px 0px repeat scroll rgb(51, 153, 0); border-bottom: 0px none; color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; line-height: 1.5em;\">如何成为全栈工程师</h2><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">1、先精后广，一专多长</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">推荐采用“<strong style=\"margin: 0px; padding: 0px;\">先精后广，一专多长</strong>”的流程来学习：<strong style=\"margin: 0px; padding: 0px;\">先在一个特定的方向上有比较深入的钻研，然后再将学习目标渐渐推广开来。</strong>比如先从前端方向入手，掌握了基本的HTML、CSS、JavaScript之后，不要转头向服务器端语言或者App方向发展，而是深入到性能优化、SEO、多种框架、响应式页面等前端细节中去。经过一到两年的深入研究之后，再去学习其他方向。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">采用这种方式来学习，不光可以触类旁通、举一反三，还让我们学习得更快，而且循序渐进更符合一般人的职业生涯发展。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); line-height: 1.5em;\"><span style=\"font-size: 14px;\">腾讯社交用户体验设计部招聘前端开发，要求如下：</span></p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">本科以上学历。</span></p></li><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">两年以上工作经验。</span></p></li><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">精通HTML、CSS、JavaScript等前端相关技术,熟悉W3C网页标准。</span></p></li><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">熟悉至少一种后台语言的开发机制(如Java、C++等)。</span></p></li><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">有一定架构能力和算法能力，有良好编码规范。</span></p></li><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">良好的学习能力、沟通能力，追求完美，有工作激情，能在较大强度下工作。</span></p></li><li><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">热爱互联网，喜欢研究各种互联网技术者更好</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 14px;\"></span></p></li></ul><p style=\"margin: 5px 0px; text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">有的竞争者提到他很擅长页面性能优化、响应式、页面渲染效率，有的写过JavaScript框架……你需要在招聘要求的方向上以200%的能力来得到这个职位。</span></p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">2、围绕商业目标</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">老板雇用一个员工，不是因为他能写程序，而是因为他能帮助自己赚钱。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">我喜欢这样的态度：对未来有自己的方向，但也知道自己没法看得太清晰。<strong style=\"margin: 0px; padding: 0px;\">对商业和市场有想法，而且自己也有足够的技术能力和自信向未来前进</strong>。</span></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 14px;\">记住，当您只有一把锤子，您看什么都是钉子。而如果您痴迷于工具，反而看不到问题所在。因此，要先看看有哪些问题需要解决，然后再补充您的工具箱。永远从商业目标的角度来决定学习哪些东西，而不是纯粹为了锻炼技术能力而去学习。</span></p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">3、用户是谁</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">这里的“用户”仍然是一个广义的定义：所有您为之服务的人。</p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">4、大巧若拙</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\"><strong style=\"margin: 0px; padding: 0px;\">大巧若拙</strong>：指真正聪明的人，不会显露自己，反面从表面看好像还很笨拙。用户体验不只是界面和交互这样可以直观感受的东西，<strong style=\"margin: 0px; padding: 0px;\">还包括一些隐藏在用户界面背后的细节和规范</strong>。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">就像冰山，露出水面的部分只占整个冰山的1/9，用户看到的只是显露出来的部分。背后的部分一般用户是看不到的：比如用户研究，用研团队会通过调查，输出一些用户画像，影响整个产品的功能方向、设计风格；还有设计规范，设计团队在设计产品的一开始制定了规范之后，新增加的功能和页面都必须遵循已有的设计规范，这样整个产品是统一的，能够给用户专业的感觉。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">我如果开创一个公司需要招聘“全栈工程师”，我要求的三个能力：<strong style=\"margin: 0px; padding: 0px;\">一专多长</strong>、<strong style=\"margin: 0px; padding: 0px;\">关注商业目标</strong>、<strong style=\"margin: 0px; padding: 0px;\">关注用户体验</strong>。</p><h2 style=\"white-space: normal; margin: 10px 0px; padding: 2px 5px; font-size: 18px; line-height: 1.5; background: none 0px 0px repeat scroll rgb(51, 153, 0); border-bottom: 0px none; color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif;\">野生程序员的故事</h2><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">野生程序员是指仅凭对计算机开发的兴趣进入这个行业，从前端到后台一手包揽，但各方面能力都不精通的人。野生程序员有很强大的单兵作战能力，但是在编入“正规军”之后，可能会不适应新的做事方法。</p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">1、Web性能优化</h3><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">压缩源码和图片</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">JavaScript文件源代码可以采用混淆压缩的方式，CSS文件源代码进行普通压缩，JPG图片可以根据具体质量来压缩为50%到70%，PNG可以使用一些开源压缩软件来压缩，比如24色变成8色、去掉一些PNG格式信息等。</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">选择合适的图片格式</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">如果图片颜色数较多就使用JPG格式，如果图片颜色数较少就使用PNG格式，如果能够通过服务器端判断浏览器支持WebP，那么就使用WebP格式和SVG格式。</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">合并静态资源</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">包括CSS、JavaScript和小图片，减少HTTP请求。</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">开启服务器端的Gzip压缩</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">这对文本资源非常有效，对图片资源则没那么大的压缩比率。</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">使用CDN</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">或者一些公开库使用第三方提供的静态资源地址（比如jQuery、normalize.css）。一方面增加并发下载量，另一方面能够和其他网站共享缓存。</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">延长静态资源缓存时间</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">这样，频繁访问网站的访客就能够更快地访问。不过，这里要通过修改文件名的方式，确保在资源更新的时候，用户会拉取到最新的内容。</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><span style=\"color: rgb(0, 0, 255);\"><strong style=\"margin: 0px; padding: 0px;\">把CSS放在页面头部，把JavaScript放在页面底部</strong></span></p></li></ul><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">这样就不会阻塞页面渲染，让页面出现长时间的空白。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">备注：每一个条目都可以进一步深层挖掘下去。Web性能优化分为服务器端和浏览器端两个方面。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">此外，由于中文的歧义性，<strong style=\"margin: 0px; padding: 0px;\">Web性能优化</strong>这个词既可以解读成<strong style=\"margin: 0px; padding: 0px;\">页面加载速度</strong>（Page Speed）的优化，也可以解读成<strong style=\"margin: 0px; padding: 0px;\">页面渲染性能</strong>的优化。或者是二者的集合。所以，应聘者如果能在这个问题上多做一些分析，会有很高的加分。但是如果你在网络性能方面的研究只是浅尝辄止，停留在压缩资源方面，这说明你还没有足够理解HTTP协议本身。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">关于<strong style=\"margin: 0px; padding: 0px;\">网络性能和HTTP协议</strong>，作为大公司的前端工程师是<strong style=\"margin: 0px; padding: 0px;\">非常看重</strong>的，因为每一个页面都会有亿万用户访问量，任何一点对服务器带宽压力都会积少成多，最终造成很大的成本。关于这方面的技术详解，我在后面会有一篇单独的文章来分析。</p><h3 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">2、知易行难</h3><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">我问一个面试者：“关于服务器端MVC架构的技术实现，您是怎样理解的？”他说：“是数据模型、视图、控制器的分离。”</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">我更进一步问道：“这种架构方式有什么好处？您在项目中是如何应用这一架构的？”他回答说：“MVC的架构方式会让项目可维护性更高，所有涉及界面的代码都在视图（View）里面，所有涉及核心逻辑的代码都在模型（Model）里面，URL路由之类的代码都在控制器（Controller）里面。我在项目中使用了MVC架构的PHP框架——CodeIgniter。”</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">我一边打开他的网站，一边继续跟他电话沟通。当看到网站的CSS代码都直接内嵌在HTML头部的时候，我忍不住问他：“为什么您的网站的CSS代码都内嵌在HTML里面呢，是使用自动化工具合并进去的吗？”他支支吾吾地说：“因为在本地调试的时候，CSS文件修改经常不生效，所以就直接在HTML里面改了，这样比较快。”</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">好吧，我想这是一个典型的“<strong style=\"margin: 0px; padding: 0px;\">知易行难</strong>”的开发者，他知道采用MVC架构的项目的可维护性更高，可是在分离样式与结构上面还没有达到最基本的要求，甚至把CSS写在HTML中。至于他说的在本地环境上发现CSS文件经常缓存，可能要看看本地服务器的缓存设置是否有问题，然后再做调试。稍微了解一点HTTP的浏览器端缓存，这就不是难事了。我更欣赏在开发流程上花工夫去理解和优化的应聘者，而不是马马虎虎，只是以完成需求为目标的人。</p><h1 style=\"white-space: normal; margin: 10px 0px; padding: 0px; font-size: 17px; line-height: 1.5; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">3、什么是“野生程序员”</h1><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">“<strong style=\"margin: 0px; padding: 0px;\">野生程序员</strong>”：就是没有计算机基础知识和相关教育经历，靠着对计算机开发的兴趣进入这个行业，虽然知识面比较广，但是各方面都一知半解的开发者。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">这几年我从一个求职者，转变成一个招聘者，有一个感受就是，中国高等教育与市场需求不接轨。学校不了解市场究竟需要什么样的人才，其设立的课程和技术往往比市场技术现状落后了5年以上。我在大学学习用ASP建站，但是现在已经几乎没有人用ASP建站了。一个直接的后果是，很多高校毕业生不能满足企业的要求。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">与此同时，中国互联网市场蓬勃发展，特别是移动互联网的发力，让中国跳过“WAP时代”，直接进入“App时代”。市场的热钱都投入到互联网行业，“BAT”等大公司不断扩张，创业公司也如雨后春笋，整个市场对软件工程师的需求缺口巨大，所以很多公司在招人的时候，没法招聘到“专业”的计算机专业毕业生。</p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin: 10px auto; white-space: normal; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-indent: 2em; line-height: 1.5em;\">最后谈谈博主我的看法,全栈工程师的自我修养在于谦虚好学,大巧若拙<span style=\"font-family: 宋体; font-size: 14px; background-color: rgb(255, 255, 255);\">。</span>学习技术在于解决问题,而不是一昧成为炫耀自己的资本<span style=\"font-family: 宋体; font-size: 14px; background-color: rgb(255, 255, 255);\">。</span></p>');
INSERT INTO `myblog_article` VALUES ('8', '1535622667.png', '【PHP】CURL扩展模拟Http请求', '1535622667', '经验', '实例分享PHP使用CURL函数模拟Http协议中的GET和POST请求,并返回数据处理,初学PHP时,不知道如何写接口的同学一定很头疼吧.........哈哈O(∩_∩)O~', '<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">GET请求其实就是页面重定向并且带上定义的参数,一般情况header()函数或者框架中的redirect()方法就可以实现,以下分享利用CURL扩展发送GET请求的代码实例:</span></p><pre class=\"brush:php;toolbar:false\">	/**\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;模拟get进行url请求\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;string&nbsp;$url\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;array&nbsp;$post_data\r\n**/\r\n	\r\nfunction&nbsp;doCurlGetRequest($url,$data,$timeout&nbsp;=&nbsp;5){\r\n\r\n	&nbsp;if($url&nbsp;==&nbsp;&quot;&quot;&nbsp;||&nbsp;$timeout&nbsp;&lt;=&nbsp;0){\r\n	&nbsp;\r\n	&nbsp;return&nbsp;false;\r\n&nbsp;}\r\n	&nbsp;$url&nbsp;=&nbsp;$url.&#39;?&#39;.http_build_query($data);	//url拼接参数\r\n	&nbsp;\r\n	&nbsp;$con&nbsp;=&nbsp;curl_init((string)$url);\r\n	&nbsp;\r\n	&nbsp;curl_setopt($con,&nbsp;CURLOPT_HEADER,&nbsp;false);\r\n	&nbsp;\r\n	&nbsp;curl_setopt($con,&nbsp;CURLOPT_RETURNTRANSFER,true);\r\n	&nbsp;\r\n	&nbsp;curl_setopt($con,&nbsp;CURLOPT_TIMEOUT,&nbsp;(int)$timeout);\r\n\r\n&nbsp;return&nbsp;curl_exec($con);\r\n}\r\n	\r\n	$url=&quot;http://www.xhyonline.com:8080&quot;;\r\n	\r\n	$data=array(&quot;name&quot;=&gt;&quot;许弘毅&quot;,&quot;age&quot;=&gt;21);\r\n	\r\n	$curl=doCurlGetRequest($url,$data);\r\n	\r\n	echo&nbsp;$curl;</pre><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">其次就是发送POST请求,之前文章中介绍过了,利用file_get_contents()这个函数模拟POST请求,虽然这个函数也能做POST请求,但在比起CURL扩展的执行效率和稳定性上来看,file_get_contents()函数远远比不上......所以我们在调用接口上,还是老老实实用CURL吧~</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">以下贴出的是CURL中模拟POST请求的代码:</span></p><pre class=\"brush:php;toolbar:false\">/**\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;模拟post进行url请求\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;string&nbsp;$url\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;array&nbsp;$post_data\r\n**/\r\n	\r\n&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;request_post($url&nbsp;=&nbsp;&#39;&#39;,&nbsp;$post_data&nbsp;=&nbsp;array())&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(empty($url)&nbsp;||&nbsp;empty($post_data))&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$o&nbsp;=&nbsp;&quot;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;(&nbsp;$post_data&nbsp;as&nbsp;$k&nbsp;=&gt;&nbsp;$v&nbsp;)&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$o.=&nbsp;&quot;$k=&quot;&nbsp;.&nbsp;urlencode(&nbsp;$v&nbsp;).&nbsp;&quot;&amp;&quot;&nbsp;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data&nbsp;=&nbsp;substr($o,0,-1);\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$postUrl&nbsp;=&nbsp;$url;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$curlPost&nbsp;=&nbsp;$post_data;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ch&nbsp;=&nbsp;curl_init();//初始化curl\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_URL,$postUrl);//抓取指定网页\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_HEADER,&nbsp;0);//设置header\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_RETURNTRANSFER,&nbsp;1);//要求结果为字符串且输出到屏幕上\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_POST,&nbsp;1);//post提交方式\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_POSTFIELDS,&nbsp;$curlPost);\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$data&nbsp;=&nbsp;curl_exec($ch);//运行curl\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_close($ch);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$data;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n	\r\n	$data=[&#39;name&#39;=&gt;&quot;兰陵&quot;,&#39;age&#39;=&gt;21];	\r\n		\r\n	$curl=request_post(&quot;http://www.xhyonline.com:8080&quot;,$data);\r\n\r\n	$json=json_decode($curl,&quot;true&quot;);\r\n	\r\n	var_dump($json);	</pre><p><span style=\"background-color: rgb(254, 254, 242); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">将函数封装起来，这样在类中调用的时候就更方便简洁了。</span></p><pre class=\"brush:php;toolbar:false\">&lt;?php\r\n	\r\nnamespace&nbsp;App\\Http\\Controllers\\Home;\r\n\r\nclass&nbsp;IndexController{\r\n	\r\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;index(){\r\n		\r\n	$url&nbsp;=&nbsp;&#39;http://mobile.jschina.com.cn/jschina/register.php&#39;;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data[&#39;appid&#39;]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;&#39;10&#39;;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data[&#39;appkey&#39;]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;&#39;cmbohpffXVR03nIpkkQXaAA1Vf5nO4nQ&#39;;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data[&#39;member_name&#39;]&nbsp;=&nbsp;&#39;zsjs124&#39;;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data[&#39;password&#39;]&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;&#39;123456&#39;;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data[&#39;email&#39;]&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;&#39;zsjs124@126.com&#39;;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//$post_data&nbsp;=&nbsp;array();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$res&nbsp;=&nbsp;$this-&gt;request_post($url,&nbsp;$post_data);&nbsp;&nbsp;&nbsp;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;print_r($res);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n	\r\n&nbsp;&nbsp;&nbsp;&nbsp;/**\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;模拟post进行url请求\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;string&nbsp;$url\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;array&nbsp;$post_data\r\n&nbsp;&nbsp;&nbsp;&nbsp;**/\r\n	\r\n&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;request_post($url&nbsp;=&nbsp;&#39;&#39;,&nbsp;$post_data&nbsp;=&nbsp;array())&nbsp;{\r\n&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(empty($url)&nbsp;||&nbsp;empty($post_data))&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$o&nbsp;=&nbsp;&quot;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;(&nbsp;$post_data&nbsp;as&nbsp;$k&nbsp;=&gt;&nbsp;$v&nbsp;)&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$o.=&nbsp;&quot;$k=&quot;&nbsp;.&nbsp;urlencode(&nbsp;$v&nbsp;).&nbsp;&quot;&amp;&quot;&nbsp;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$post_data&nbsp;=&nbsp;substr($o,0,-1);\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$postUrl&nbsp;=&nbsp;$url;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$curlPost&nbsp;=&nbsp;$post_data;\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ch&nbsp;=&nbsp;curl_init();//初始化curl\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_URL,$postUrl);//抓取指定网页\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_HEADER,&nbsp;0);//设置header\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_RETURNTRANSFER,&nbsp;1);//要求结果为字符串且输出到屏幕上\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_POST,&nbsp;1);//post提交方式\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_POSTFIELDS,&nbsp;$curlPost);\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$data&nbsp;=&nbsp;curl_exec($ch);//运行curl\r\n		\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_close($ch);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$data;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n}\r\n\r\n?&gt;</pre>');
INSERT INTO `myblog_article` VALUES ('11', '1536333246.jpg', '【前端练习】二手商城界面开发', '1536333246', '作品', '之前闲来无事写的界面', '<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">首页界面</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20180907/1536333320891084.jpg\" title=\"1536333320891084.jpg\" alt=\"1.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20180907/1536333420294261.png\" title=\"1536333420294261.png\" alt=\"2.png\"/></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">商品展示页面</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20180907/1536333463367229.jpg\" title=\"1536333463367229.jpg\" alt=\"3.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">个人中心界面</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20180907/1536333518789273.jpg\" title=\"1536333518789273.jpg\" alt=\"4.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">下单界面</span><br/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20180907/1536333573460585.jpg\" title=\"1536333573460585.jpg\" alt=\"5.jpg\"/></span></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">分类界面<br/></span></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20180907/1536333605976746.jpg\" title=\"1536333605976746.jpg\" alt=\"6.jpg\"/></span></span></p><p><span style=\"font-family:微软雅黑, Microsoft YaHei\"><span style=\"font-size: 14px;\">因为作者日常懒,后端功能就没有继续往下写了,审美能力有限,只能当做一次练习作业吧....</span></span></p><p><span style=\"font-family:微软雅黑, Microsoft YaHei\"><span style=\"font-size: 14px;\">参观入口:<a href=\"http://abc.xhyonline.com/shop\" target=\"_self\">入口点击</a></span></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p>');
INSERT INTO `myblog_article` VALUES ('10', '1536064683.jpg', '【Linux】基本操作以及文件查找', '1536064683', '笔记', '补齐博客界面重构后的Linux笔记', '<pre class=\"brush:bash;toolbar:false\">常见的目录的操作指令\r\n\r\ncd&nbsp;/	切换到根目录\r\n\r\nls		查看目录\r\n\r\ntree&nbsp;bin&nbsp;查看目录\r\n\r\nmkdir&nbsp;123	在当前目录下建立123文件夹\r\n\r\nmkdir&nbsp;-p&nbsp;123/456&nbsp;批量创建文件夹\r\n\r\n(注)mkdir&nbsp;-p&nbsp;/123/456&nbsp;和&nbsp;mkdir&nbsp;-p&nbsp;123/456意思代表不一样,前者代表在跟目录,后者是当前目录\r\n\r\nrmdir&nbsp;123	删除当前目录下的123目录,只能删除空目录\r\n\r\nrm&nbsp;-rf&nbsp;强制性删除目录或者文件//推荐用(方便)\r\n\r\nmv&nbsp;index.html&nbsp;default.html&nbsp;修改文件名(mv移动文件,原理是将当前文件夹下的index.html文件,移动到当前的文件夹下,并且改名为default.html)\r\n\r\n\r\n\r\n文件操作指令\r\n\r\ntouch&nbsp;123	新建123文件\r\n\r\ntouch&nbsp;file{1..10}&nbsp;批量创建文件&nbsp;创建文件file1,file2.....到file1000\r\n\r\nrm&nbsp;-rf&nbsp;file{1..1000}	批量删除文件\r\n\r\nrm&nbsp;123		删除123文件	(-f)&nbsp;强制性删除,例:	rm&nbsp;-f&nbsp;123\r\n\r\ncat	123		查看123文件	\r\n\r\ncat&nbsp;123&nbsp;|&nbsp;tail&nbsp;-3	//查看文件123的后三行\r\n\r\ncat&nbsp;123&nbsp;|&nbsp;head&nbsp;-3	//查看文件前三行\r\n\r\ncat&nbsp;httpd.conf&nbsp;|&nbsp;grep&nbsp;listen&nbsp;查看httpd.conf的文件中包含listen这个单词的那几行&nbsp;\r\n\r\ncp&nbsp;readme&nbsp;/ftp_root&nbsp;	将当前目录下的readme文件复制到&nbsp;根目录下的ftp_root目录\r\n\r\ncp&nbsp;index.html&nbsp;index.html.bak	将当前目录下index.html文件拷贝一份在当前文件夹,并且命名为index.html.bak\r\n\r\ncd&nbsp;-a&nbsp;/ftp_root&nbsp;/home/backup	将根目录下的ftp_root目录复制到&nbsp;根目录下的home文件夹下的backup文件夹下,且不管有没有backup文件夹,它都能主动创建一个\r\n\r\n同理\r\n\r\ncd&nbsp;-a&nbsp;/home/backup/ftp_root&nbsp;/	将backup中的ftp_root文件夹复制到根目录下\r\n\r\n文件写入操作\r\n\r\nvi&nbsp;index.html&nbsp;在index.html中书写\r\n\r\n进入后书写,按esc退出书写模式后按:wq保存\r\n\r\ninit&nbsp;0&nbsp;关机\r\n\r\ninit&nbsp;6&nbsp;重启\r\n\r\npwd&nbsp;查看当前路径\r\n\r\ncd&nbsp;-&nbsp;切换最近的两次目录\r\n\r\nclear	清空屏幕\r\n\r\nctrl+c	中断当前操作\r\n\r\nhistory&nbsp;查看历史命令\r\n\r\n\r\n查找文件命令\r\n\r\n方式一:\r\n\r\nfind&nbsp;/&nbsp;-name&nbsp;httpd.conf	在根目录下查找一个叫http.conf的文件\r\n\r\n方式二\r\n\r\nupdatedb\r\n\r\nlocate&nbsp;文件名</pre>');
INSERT INTO `myblog_article` VALUES ('12', '1536384315.jpg', '手机端页面在项目中遇到的一些问题及解决办法', '1536384315', '经验', '分享一些关于手机端页面在项目中遇到的一些问题及解决办法.......', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">1.解决页面使用overflow: scroll在iOS上滑动卡顿的问题?</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 0em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">首先你可能会给页面的html和body增加了height: 100%, 然后就可能造成IOS上页面滑动的卡顿问题。解决方案是：</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 0em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(1) 看是否能把body和html的height: 100%去除掉。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 0em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 0em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(2) 在滚动的容器中增加：-webkit-overflow-scrolling: touch或者给body增加：body {overflow-x: hidden}。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">2.ios页面橡皮弹回效果遮挡页面选项卡？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(1) 有时body和html的height: 100%去除掉问题可能就没有了。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(2) 到达临界值的时候在阻止事件默认行为</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><pre class=\"brush:js;toolbar:false\">var&nbsp;startY,endY;\r\n\r\n//记录手指触摸的起点坐标\r\n\r\n$(&#39;body&#39;).on(&#39;touchstart&#39;,function&nbsp;(e)&nbsp;{\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;startY&nbsp;=&nbsp;e.touches[0].pageY;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n});\r\n\r\n$(&#39;body&#39;).on(&#39;touchmove&#39;,function&nbsp;(e)&nbsp;{\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;endY&nbsp;=&nbsp;e.touches[0].pageY;&nbsp;&nbsp;//记录手指触摸的移动中的坐标\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//手指下滑，页面到达顶端不能继续下滑\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(endY&gt;startY&amp;&amp;&nbsp;$(window).scrollTop()&lt;=0){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;e.preventDefault();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;//手指上滑，页面到达底部能继续上滑\r\n&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(endY&lt;startY&amp;&amp;&nbsp;$(window).scrollTop()+&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(window).height()&gt;=$(&#39;body&#39;)[0].scrollHeight){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;e.preventDefault();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n})</pre><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">有时也会碰见弹窗出来后两个层的橡皮筋效果出现问题，我们可以在弹出弹出时给底层页面加上一个类名，类名禁止页面滑动这样下层的橡皮筋效果就会被禁止，就不会影响弹窗层。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">3.IOS机型margin属性无效问题？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(1) 设置html body的高度为百分比时，margin-bottom在safari里失效</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(2) 直接padding代替margin</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">4.Ios绑定点击事件不执行？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(1)添加样式cursor ：pointer。点击后消除背景闪一下的css：-webkit-tap-highlight-color:transparent;</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">5.Ios键盘换行变为搜索？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">首先，input 要放在 form里面。</span></p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">这时 &quot;换行&quot; 已经变成 “前往”。</span></p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">如果想变成 “搜索”，input 设置 type=&quot;search&quot;</span></p></li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><strong style=\"color: rgb(255, 0, 0); margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"></span><strong style=\"color: rgb(255, 0, 0); margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">6.Jq对a标签点击事件不生效？</span></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">出现这种情况的原因不明，有的朋友解释：我们平时都是点击的A标签中的文字了。 所以要想用JS模拟点击A标签事件，就得先往A标签中的文字添加能被JS捕获的元素，然后再用JS模拟点击该元素即可。但是我觉得不合理，虽然找不到原因但是解决办法还是有的。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(1)document.getElementById(&quot;abc &quot;).click();</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(2)$(&quot;#abc &quot;)[0].click();</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">7.有时因为服务器或者别的原因导致页面上的图片没有找到？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">这是我们想需要用一个本地的图片代替没有找的的图片</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><pre class=\"brush:js;toolbar:false\">&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;&nbsp;\r\n\r\nfunction&nbsp;nofind(){&nbsp;\r\n\r\nvar&nbsp;img=event.srcElement;&nbsp;\r\n\r\nimg.src=&quot;images/logoError.png&quot;;&nbsp;\r\n\r\nimg.onerror=null;&nbsp;控制不要一直跳动&nbsp;\r\n\r\n}&nbsp;\r\n&lt;/script&gt;&nbsp;\r\n\r\n&lt;img&nbsp;src=&quot;images/logo.png&quot;&nbsp;onerror=&quot;nofind();&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">8.transform属性影响position:fixed？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">规范中有规定：如果元素的transform值不为none，则该元素会生成包含块和层叠上下文。CSS Transforms Module Level 1不只在手机上，电脑上也一样。除了fixed元素会受影响之外，z-index（层叠上下文）值也会受影响。绝对定位元素等和包含块有关的属性都会受到影响。当然如果transform元素的display值为inline时又会有所不同。最简单的解决方法就是transform元素内部不能有absolute、fixed元素.</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">9.ios对position: fixed不太友好，有时我们需要加点处理？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; text-indent: 2em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">在安卓上面，点击页面底部的输入框，软键盘弹出，页面移动上移。</span><span style=\"font-size: 15px; text-indent: 2em;\">而ios上面，点击页面底部输入框，软键盘弹出，输入框看不到了。。。查资料说什么的都有，iscroll,jquery-moblie，absolute，fixe，static都非常复杂，要改很多。。。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">让他弹出时让滚动条在最低部</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></p><pre class=\"brush:js;toolbar:false\">var&nbsp;u&nbsp;=&nbsp;navigator.userAgent,&nbsp;app&nbsp;=&nbsp;navigator.appVersion;\r\n\r\nvar&nbsp;isiOS&nbsp;=&nbsp;!!u.match(/\\(i[^;]+;(&nbsp;U;)?&nbsp;CPU.+Mac&nbsp;OS&nbsp;X/);&nbsp;//ios终端\r\n\r\nif&nbsp;(isiOS)&nbsp;{\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(&#39;textarea&#39;).focus(function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.setTimeout(&#39;scrollBottom()&#39;,&nbsp;500);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n}\r\nfunction&nbsp;scrollBottom()&nbsp;{\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;window.scrollTo(0,&nbsp;$(&#39;body&#39;).height());\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n}</pre><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">10.jq validate插件验证问题？</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\"><br/></span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 15px;\">(1)所以的input必须有name不然会出错</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !i